contracts/chainlink/SwarmCCIP.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@chainlink/contracts-ccip/contracts/applications/CCIPReceiver.sol";
import "@chainlink/contracts-ccip/contracts/interfaces/IRouterClient.sol";
import "@chainlink/contracts-ccip/contracts/libraries/Client.sol";
import "@chainlink/contracts/src/v0.8/shared/access/ConfirmedOwner.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title SwarmCCIP
 * @dev Cross-chain infrastructure for Swarm Oracle using Chainlink CCIP
 */
contract SwarmCCIP is CCIPReceiver, ConfirmedOwner, ReentrancyGuard, Pausable {
    enum MessageType {
        MARKET_SYNC,
        LIQUIDITY_BRIDGE,
        AGENT_COORDINATION,
        RESOLUTION_SYNC
    }

    struct CrossChainMarket {
        uint256 localMarketId;
        uint256 remoteMarketId;
        uint64 remoteChainSelector;
        address remoteContract;
        uint256 totalLiquidity;
        bool active;
    }

    struct LiquidityBridge {
        address user;
        uint256 amount;
        uint64 sourceChain;
        uint64 destinationChain;
        uint256 timestamp;
        bool completed;
    }

    struct AgentMessage {
        address agent;
        uint256 marketId;
        bytes data;
        uint256 timestamp;
        MessageType messageType;
    }

    // State variables
    mapping(uint64 => address) public chainContracts; // chainSelector => contract address
    mapping(uint256 => CrossChainMarket) public crossChainMarkets;
    mapping(bytes32 => LiquidityBridge) public liquidityBridges;
    mapping(address => bool) public authorizedAgents;
    mapping(uint64 => bool) public supportedChains;

    uint256 public nextBridgeId = 1;
    uint256 public bridgeFee = 1000; // 10% in basis points
    address public liquidityPool;
    IERC20 public bridgeToken;

    // Events
    event CrossChainMarketCreated(
        uint256 indexed localMarketId,
        uint256 indexed remoteMarketId,
        uint64 indexed remoteChain
    );

    event LiquidityBridgeInitiated(
        bytes32 indexed bridgeId,
        address indexed user,
        uint256 amount,
        uint64 sourceChain,
        uint64 destinationChain
    );

    event LiquidityBridgeCompleted(
        bytes32 indexed bridgeId,
        address indexed user,
        uint256 amount
    );

    event CrossChainMessageSent(
        bytes32 indexed messageId,
        uint64 indexed destinationChain,
        MessageType messageType
    );

    event CrossChainMessageReceived(
        bytes32 indexed messageId,
        uint64 indexed sourceChain,
        MessageType messageType
    );

    event CrossChainMarketSynced(
        uint256 indexed marketId,
        uint64 indexed sourceChain,
        uint256 totalVolume,
        uint256 avgPrice,
        int256 sentiment
    );

    event CrossChainAgentPrediction(
        uint256 indexed marketId,
        uint64 indexed sourceChain,
        address indexed agent,
        bool prediction,
        uint256 confidence,
        string reasoning
    );

    event CrossChainMarketResolved(
        uint256 indexed marketId,
        uint64 indexed sourceChain,
        bool outcome,
        uint256 totalVolume,
        uint256 resolutionTime,
        string evidence
    );

    error UnsupportedChain(uint64 chainSelector);
    error InsufficientLiquidity();
    error UnauthorizedAgent();
    error InvalidBridgeAmount();

    constructor(
        address _router,
        address _bridgeToken,
        address _liquidityPool
    ) CCIPReceiver(_router) ConfirmedOwner(msg.sender) {
        bridgeToken = IERC20(_bridgeToken);
        liquidityPool = _liquidityPool;
    }

    /**
     * @dev Create a cross-chain market link
     */
    function createCrossChainMarket(
        uint256 _localMarketId,
        uint256 _remoteMarketId,
        uint64 _remoteChainSelector
    ) external onlyOwner {
        require(supportedChains[_remoteChainSelector], "Unsupported chain");

        crossChainMarkets[_localMarketId] = CrossChainMarket({
            localMarketId: _localMarketId,
            remoteMarketId: _remoteMarketId,
            remoteChainSelector: _remoteChainSelector,
            remoteContract: chainContracts[_remoteChainSelector],
            totalLiquidity: 0,
            active: true
        });

        emit CrossChainMarketCreated(_localMarketId, _remoteMarketId, _remoteChainSelector);
    }

    /**
     * @dev Bridge liquidity to another chain
     */
    function bridgeLiquidity(
        uint256 _amount,
        uint64 _destinationChain,
        address _receiver
    ) external payable nonReentrant whenNotPaused returns (bytes32 bridgeId) {
        require(supportedChains[_destinationChain], "Unsupported destination chain");
        require(_amount > 0, "Invalid amount");

        // Transfer tokens from user
        bridgeToken.transferFrom(msg.sender, address(this), _amount);

        // Calculate fees
        uint256 fee = (_amount * bridgeFee) / 10000;
        uint256 bridgedAmount = _amount - fee;

        // Generate bridge ID
        bridgeId = keccak256(abi.encodePacked(msg.sender, _amount, block.timestamp, nextBridgeId++));

        // Store bridge request
        liquidityBridges[bridgeId] = LiquidityBridge({
            user: msg.sender,
            amount: bridgedAmount,
            sourceChain: _getChainSelector(),
            destinationChain: _destinationChain,
            timestamp: block.timestamp,
            completed: false
        });

        // Prepare CCIP message
        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
            receiver: abi.encode(chainContracts[_destinationChain]),
            data: abi.encode(MessageType.LIQUIDITY_BRIDGE, abi.encode(bridgeId, _receiver, bridgedAmount)),
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: "",
            feeToken: address(0) // Pay in native token
        });

        // Send cross-chain message
        bytes32 messageId = IRouterClient(i_ccipRouter).ccipSend{value: msg.value}(
            _destinationChain,
            message
        );

        emit LiquidityBridgeInitiated(bridgeId, msg.sender, _amount, _getChainSelector(), _destinationChain);
        emit CrossChainMessageSent(messageId, _destinationChain, MessageType.LIQUIDITY_BRIDGE);

        return bridgeId;
    }

    /**
     * @dev Sync market data across chains
     */
    function syncMarketData(
        uint256 _marketId,
        uint64 _destinationChain,
        bytes memory _marketData
    ) external payable onlyAuthorizedAgent {
        require(supportedChains[_destinationChain], "Unsupported destination chain");

        // Prepare CCIP message
        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
            receiver: abi.encode(chainContracts[_destinationChain]),
            data: abi.encode(MessageType.MARKET_SYNC, abi.encode(_marketId, _marketData)),
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: "",
            feeToken: address(0)
        });

        // Send cross-chain message
        bytes32 messageId = IRouterClient(i_ccipRouter).ccipSend{value: msg.value}(
            _destinationChain,
            message
        );

        emit CrossChainMessageSent(messageId, _destinationChain, MessageType.MARKET_SYNC);
    }

    /**
     * @dev Send agent coordination message
     */
    function sendAgentCoordination(
        uint64 _destinationChain,
        uint256 _marketId,
        bytes memory _coordinationData
    ) external payable onlyAuthorizedAgent {
        require(supportedChains[_destinationChain], "Unsupported destination chain");

        // Prepare CCIP message
        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
            receiver: abi.encode(chainContracts[_destinationChain]),
            data: abi.encode(MessageType.AGENT_COORDINATION, abi.encode(_marketId, msg.sender, _coordinationData)),
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: "",
            feeToken: address(0)
        });

        // Send cross-chain message
        bytes32 messageId = IRouterClient(i_ccipRouter).ccipSend{value: msg.value}(
            _destinationChain,
            message
        );

        emit CrossChainMessageSent(messageId, _destinationChain, MessageType.AGENT_COORDINATION);
    }

    /**
     * @dev Sync market resolution across chains
     */
    function syncMarketResolution(
        uint256 _marketId,
        uint64 _destinationChain,
        bool _outcome,
        bytes memory _resolutionData
    ) external payable onlyOwner {
        require(supportedChains[_destinationChain], "Unsupported destination chain");

        // Prepare CCIP message
        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
            receiver: abi.encode(chainContracts[_destinationChain]),
            data: abi.encode(MessageType.RESOLUTION_SYNC, abi.encode(_marketId, _outcome, _resolutionData)),
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: "",
            feeToken: address(0)
        });

        // Send cross-chain message
        bytes32 messageId = IRouterClient(i_ccipRouter).ccipSend{value: msg.value}(
            _destinationChain,
            message
        );

        emit CrossChainMessageSent(messageId, _destinationChain, MessageType.RESOLUTION_SYNC);
    }

    /**
     * @dev Handle received CCIP messages
     */
    function _ccipReceive(Client.Any2EVMMessage memory message) internal override {
        uint64 sourceChain = message.sourceChainSelector;
        bytes32 messageId = message.messageId;

        // Decode message type and data
        (MessageType messageType, bytes memory data) = abi.decode(message.data, (MessageType, bytes));

        if (messageType == MessageType.LIQUIDITY_BRIDGE) {
            _handleLiquidityBridge(data, sourceChain);
        } else if (messageType == MessageType.MARKET_SYNC) {
            _handleMarketSync(data, sourceChain);
        } else if (messageType == MessageType.AGENT_COORDINATION) {
            _handleAgentCoordination(data, sourceChain);
        } else if (messageType == MessageType.RESOLUTION_SYNC) {
            _handleResolutionSync(data, sourceChain);
        }

        emit CrossChainMessageReceived(messageId, sourceChain, messageType);
    }

    /**
     * @dev Handle liquidity bridge completion
     */
    function _handleLiquidityBridge(bytes memory data, uint64 sourceChain) internal {
        (bytes32 bridgeId, address receiver, uint256 amount) = abi.decode(data, (bytes32, address, uint256));

        // Mint or transfer tokens to receiver
        bridgeToken.transfer(receiver, amount);

        emit LiquidityBridgeCompleted(bridgeId, receiver, amount);
    }

    /**
     * @dev Handle market data sync
     */
    function _handleMarketSync(bytes memory data, uint64 sourceChain) internal {
        (uint256 marketId, bytes memory marketData) = abi.decode(data, (uint256, bytes));

        // Decode market sync data (totalVolume, avgPrice, sentiment)
        (uint256 totalVolume, uint256 avgPrice, int256 sentiment) = abi.decode(marketData, (uint256, uint256, int256));

        // Store cross-chain market data for this market
        CrossChainMarket storage market = crossChainMarkets[marketId];
        if (market.active) {
            market.totalLiquidity = totalVolume;

            // Emit event for external systems to process
            emit CrossChainMarketSynced(marketId, sourceChain, totalVolume, avgPrice, sentiment);
        }
    }

    /**
     * @dev Handle agent coordination
     */
    function _handleAgentCoordination(bytes memory data, uint64 sourceChain) internal {
        (uint256 marketId, address agent, bytes memory coordinationData) = abi.decode(data, (uint256, address, bytes));

        // Decode agent prediction data (prediction, confidence, reasoning)
        (bool prediction, uint256 confidence, string memory reasoning) = abi.decode(coordinationData, (bool, uint256, string));

        // Store cross-chain agent prediction
        CrossChainMarket storage market = crossChainMarkets[marketId];
        if (market.active && authorizedAgents[agent]) {
            // Emit event for agent coordination
            emit CrossChainAgentPrediction(marketId, sourceChain, agent, prediction, confidence, reasoning);
        }
    }

    /**
     * @dev Handle resolution sync
     */
    function _handleResolutionSync(bytes memory data, uint64 sourceChain) internal {
        (uint256 marketId, bool outcome, bytes memory resolutionData) = abi.decode(data, (uint256, bool, bytes));

        // Decode resolution metadata (totalVolume, timestamp, evidence)
        (uint256 totalVolume, uint256 resolutionTime, string memory evidence) = abi.decode(resolutionData, (uint256, uint256, string));

        // Update cross-chain market resolution
        CrossChainMarket storage market = crossChainMarkets[marketId];
        if (market.active) {
            market.totalLiquidity = totalVolume;

            // Emit event for cross-chain resolution
            emit CrossChainMarketResolved(marketId, sourceChain, outcome, totalVolume, resolutionTime, evidence);
        }
    }

    /**
     * @dev Calculate CCIP fees
     */
    function calculateCCIPFee(
        uint64 _destinationChain,
        bytes memory _data
    ) external view returns (uint256 fee) {
        Client.EVM2AnyMessage memory message = Client.EVM2AnyMessage({
            receiver: abi.encode(chainContracts[_destinationChain]),
            data: _data,
            tokenAmounts: new Client.EVMTokenAmount[](0),
            extraArgs: "",
            feeToken: address(0)
        });

        fee = IRouterClient(i_ccipRouter).getFee(_destinationChain, message);
    }

    /**
     * @dev Get current chain selector
     */
    function _getChainSelector() internal view returns (uint64) {
        if (block.chainid == 1) return 5009297550715157269; // Ethereum
        if (block.chainid == 137) return 4051577828743386545; // Polygon
        if (block.chainid == 42161) return 4949039107694359620; // Arbitrum
        if (block.chainid == 8453) return 15971525489660198786; // Base
        revert("Unsupported chain");
    }

    /**
     * @dev Add supported chain
     */
    function addSupportedChain(uint64 _chainSelector, address _contractAddress) external onlyOwner {
        supportedChains[_chainSelector] = true;
        chainContracts[_chainSelector] = _contractAddress;
    }

    /**
     * @dev Remove supported chain
     */
    function removeSupportedChain(uint64 _chainSelector) external onlyOwner {
        supportedChains[_chainSelector] = false;
        delete chainContracts[_chainSelector];
    }

    /**
     * @dev Authorize agent
     */
    function setAuthorizedAgent(address _agent, bool _authorized) external onlyOwner {
        authorizedAgents[_agent] = _authorized;
    }

    /**
     * @dev Update bridge fee
     */
    function setBridgeFee(uint256 _fee) external onlyOwner {
        require(_fee <= 1000, "Fee too high"); // Max 10%
        bridgeFee = _fee;
    }

    /**
     * @dev Update liquidity pool
     */
    function setLiquidityPool(address _pool) external onlyOwner {
        liquidityPool = _pool;
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Withdraw native tokens
     */
    function withdrawNative() external onlyOwner {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            payable(owner()).transfer(balance);
        }
    }

    /**
     * @dev Withdraw ERC20 tokens
     */
    function withdrawTokens(address token, uint256 amount) external onlyOwner {
        IERC20(token).transfer(owner(), amount);
    }

    /**
     * @dev Get cross-chain market info
     */
    function getCrossChainMarket(uint256 _marketId) external view returns (CrossChainMarket memory) {
        return crossChainMarkets[_marketId];
    }

    /**
     * @dev Get liquidity bridge info
     */
    function getLiquidityBridge(bytes32 _bridgeId) external view returns (LiquidityBridge memory) {
        return liquidityBridges[_bridgeId];
    }

    modifier onlyAuthorizedAgent() {
        if (!authorizedAgents[msg.sender] && msg.sender != owner()) {
            revert UnauthorizedAgent();
        }
        _;
    }

    // Allow contract to receive native tokens for CCIP fees
    receive() external payable {}
}

contracts/chainlink/SwarmFunctions.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@chainlink/contracts/src/v0.8/functions/v1_0_0/FunctionsClient.sol";
import "@chainlink/contracts/src/v0.8/functions/v1_0_0/libraries/FunctionsRequest.sol";
import "@chainlink/contracts/src/v0.8/shared/access/ConfirmedOwner.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title SwarmFunctions
 * @dev Chainlink Functions integration for external data aggregation
 */
contract SwarmFunctions is FunctionsClient, ConfirmedOwner, ReentrancyGuard {
    using FunctionsRequest for FunctionsRequest.Request;

    struct DataRequest {
        uint256 requestId;
        string dataType; // "twitter_sentiment", "news_sentiment", "market_data"
        string[] parameters;
        uint256 timestamp;
        address requester;
        bool fulfilled;
    }

    struct SentimentData {
        int256 score; // -10000 to 10000 (-100.00% to 100.00%)
        uint256 volume; // Number of mentions/articles
        uint256 timestamp;
        string source; // "twitter", "reddit", "news"
    }

    struct MarketData {
        uint256 price; // In wei (scaled)
        uint256 volume24h;
        int256 priceChange24h; // -10000 to 10000 (-100.00% to 100.00%)
        uint256 timestamp;
        string asset; // "BTC", "ETH", etc.
    }

    // State variables
    mapping(bytes32 => DataRequest) public dataRequests;
    mapping(string => SentimentData) public sentimentData; // key: "twitter_BTC", "news_ETH"
    mapping(string => MarketData) public marketData; // key: "BTC", "ETH"
    mapping(address => bool) public authorizedCallers;

    bytes32 public donId;
    uint64 public subscriptionId;
    uint32 public gasLimit = 300000;

    // JavaScript source code for different data types
    string public twitterSentimentSource;
    string public newsSentimentSource;
    string public marketDataSource;

    // Events
    event DataRequested(
        bytes32 indexed requestId,
        string dataType,
        string[] parameters,
        address requester
    );

    event DataFulfilled(
        bytes32 indexed requestId,
        string dataType,
        bytes response
    );

    event SentimentUpdated(
        string indexed key,
        int256 score,
        uint256 volume,
        string source
    );

    event MarketDataUpdated(
        string indexed asset,
        uint256 price,
        uint256 volume24h,
        int256 priceChange24h
    );

    error UnauthorizedCaller();
    error InvalidDataType();
    error RequestNotFound();

    constructor(
        address router,
        bytes32 _donId,
        uint64 _subscriptionId
    ) FunctionsClient(router) ConfirmedOwner(msg.sender) {
        donId = _donId;
        subscriptionId = _subscriptionId;

        // Initialize JavaScript source codes
        _initializeSourceCodes();
    }

    /**
     * @dev Initialize JavaScript source codes for different data types
     */
    function _initializeSourceCodes() internal {
        // Real Twitter/X sentiment analysis using official API
        twitterSentimentSource = 
            "const bearerToken = secrets.twitterBearerToken;"
            "const asset = args[0];"
            "const query = `${asset} (crypto OR cryptocurrency) -is:retweet lang:en`;"
            ""
            "const response = await Functions.makeHttpRequest({"
            "  url: 'https://api.twitter.com/2/tweets/search/recent',"
            "  method: 'GET',"
            "  headers: { 'Authorization': `Bearer ${bearerToken}` },"
            "  params: {"
            "    'query': query,"
            "    'max_results': 100,"
            "    'tweet.fields': 'public_metrics,created_at,context_annotations'"
            "  }"
            "});"
            ""
            "if (!response.data || !response.data.data) {"
            "  return Functions.encodeUint256(0) + Functions.encodeUint256(0);"
            "}"
            ""
            "let sentiment = 0;"
            "let totalEngagement = 0;"
            "const tweets = response.data.data;"
            ""
            "for (const tweet of tweets) {"
            "  const text = tweet.text.toLowerCase();"
            "  const metrics = tweet.public_metrics;"
            "  const engagement = metrics.retweet_count + metrics.like_count + metrics.reply_count;"
            "  "
            "  // Weighted sentiment based on engagement"
            "  let tweetSentiment = 0;"
            "  if (text.match(/\\b(moon|bullish|pump|rally|surge|green|up|buy|hodl|diamond)\\b/g)) tweetSentiment += 2;"
            "  if (text.match(/\\b(bear|dump|crash|red|down|sell|rekt|liquidat)\\b/g)) tweetSentiment -= 2;"
            "  if (text.match(/\\b(good|great|amazing|excellent|positive)\\b/g)) tweetSentiment += 1;"
            "  if (text.match(/\\b(bad|terrible|awful|negative|worried)\\b/g)) tweetSentiment -= 1;"
            "  "
            "  sentiment += tweetSentiment * Math.log(engagement + 1);"
            "  totalEngagement += engagement;"
            "}"
            ""
            "const normalizedSentiment = tweets.length > 0 ? Math.round((sentiment / tweets.length) * 1000) : 0;"
            "const volumeScore = Math.min(tweets.length * 10, 10000);"
            ""
            "return Functions.encodeUint256(Math.max(-10000, Math.min(10000, normalizedSentiment))) + "
            "       Functions.encodeUint256(volumeScore);";

        // Real News sentiment analysis using NewsAPI
        newsSentimentSource = 
            "const apiKey = secrets.newsApiKey;"
            "const asset = args[0];"
            "const query = `${asset} AND (cryptocurrency OR crypto OR blockchain)`;"
            ""
            "const response = await Functions.makeHttpRequest({"
            "  url: 'https://newsapi.org/v2/everything',"
            "  method: 'GET',"
            "  params: {"
            "    'q': query,"
            "    'language': 'en',"
            "    'sortBy': 'publishedAt',"
            "    'pageSize': 50,"
            "    'apiKey': apiKey"
            "  }"
            "});"
            ""
            "if (!response.data || !response.data.articles) {"
            "  return Functions.encodeUint256(0) + Functions.encodeUint256(0);"
            "}"
            ""
            "let sentiment = 0;"
            "let totalScore = 0;"
            "const articles = response.data.articles;"
            ""
            "for (const article of articles) {"
            "  const title = (article.title || '').toLowerCase();"
            "  const description = (article.description || '').toLowerCase();"
            "  const content = title + ' ' + description;"
            "  "
            "  let articleSentiment = 0;"
            "  // Positive indicators"
            "  if (content.match(/\\b(surge|rally|gains?|rise|bull|positive|growth|adoption|breakthrough)\\b/g)) articleSentiment += 3;"
            "  if (content.match(/\\b(increase|up|high|strong|optimistic|bullish)\\b/g)) articleSentiment += 2;"
            "  if (content.match(/\\b(good|better|improved|promising|potential)\\b/g)) articleSentiment += 1;"
            "  "
            "  // Negative indicators"
            "  if (content.match(/\\b(crash|plunge|dump|bear|collapse|fraud|hack|banned?)\\b/g)) articleSentiment -= 3;"
            "  if (content.match(/\\b(fall|drop|decline|down|weak|concern|risk)\\b/g)) articleSentiment -= 2;"
            "  if (content.match(/\\b(bad|worse|negative|warning|volatile)\\b/g)) articleSentiment -= 1;"
            "  "
            "  // Weight by source credibility (rough heuristic)"
            "  const source = (article.source.name || '').toLowerCase();"
            "  let weight = 1;"
            "  if (source.includes('reuters') || source.includes('bloomberg') || source.includes('wsj')) weight = 2;"
            "  if (source.includes('coindesk') || source.includes('cointelegraph')) weight = 1.5;"
            "  "
            "  sentiment += articleSentiment * weight;"
            "  totalScore += Math.abs(articleSentiment) * weight;"
            "}"
            ""
            "const normalizedSentiment = totalScore > 0 ? Math.round((sentiment / totalScore) * 10000) : 0;"
            "const volumeScore = Math.min(articles.length * 20, 10000);"
            ""
            "return Functions.encodeUint256(Math.max(-10000, Math.min(10000, normalizedSentiment))) + "
            "       Functions.encodeUint256(volumeScore);";

        // Real Market data using CoinGecko API (free tier)
        marketDataSource = 
            "const asset = args[0];"
            "let coinId;"
            "if (asset === 'BTC') coinId = 'bitcoin';"
            "else if (asset === 'ETH') coinId = 'ethereum';"
            "else if (asset === 'LINK') coinId = 'chainlink';"
            "else if (asset === 'UNI') coinId = 'uniswap';"
            "else coinId = asset.toLowerCase();"
            ""
            "// Get current price and 24h data"
            "const priceResponse = await Functions.makeHttpRequest({"
            "  url: `https://api.coingecko.com/api/v3/simple/price`,"
            "  method: 'GET',"
            "  params: {"
            "    'ids': coinId,"
            "    'vs_currencies': 'usd',"
            "    'include_24hr_vol': 'true',"
            "    'include_24hr_change': 'true',"
            "    'include_market_cap': 'true'"
            "  }"
            "});"
            ""
            "if (!priceResponse.data || !priceResponse.data[coinId]) {"
            "  throw new Error('Price data not found for ' + asset);"
            "}"
            ""
            "const data = priceResponse.data[coinId];"
            "const currentPrice = data.usd;"
            "const volume24h = data.usd_24h_vol || 0;"
            "const priceChange24h = data.usd_24h_change || 0;"
            ""
            "// Convert to contract format"
            "const priceWei = Math.round(currentPrice * 1e18);"
            "const volumeWei = Math.round(volume24h * 1e18);"
            "const priceChangeBps = Math.round(priceChange24h * 100); // basis points"
            ""
            "return Functions.encodeUint256(priceWei) + "
            "       Functions.encodeUint256(volumeWei) + "
            "       Functions.encodeInt256(priceChangeBps);";
    }

    /**
     * @dev Request Twitter sentiment data
     */
    function requestTwitterSentiment(string[] calldata assets) external onlyAuthorized returns (bytes32 requestId) {
        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(twitterSentimentSource);
        req.setArgs(assets);
        req.addSecretsReference("twitter");

        requestId = _sendRequest(req.encodeCBOR(), subscriptionId, gasLimit, donId);

        dataRequests[requestId] = DataRequest({
            requestId: uint256(requestId),
            dataType: "twitter_sentiment",
            parameters: assets,
            timestamp: block.timestamp,
            requester: msg.sender,
            fulfilled: false
        });

        emit DataRequested(requestId, "twitter_sentiment", assets, msg.sender);
        return requestId;
    }

    /**
     * @dev Request news sentiment data
     */
    function requestNewsSentiment(string[] calldata assets) external onlyAuthorized returns (bytes32 requestId) {
        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(newsSentimentSource);
        req.setArgs(assets);
        req.addSecretsReference("news");

        requestId = _sendRequest(req.encodeCBOR(), subscriptionId, gasLimit, donId);

        dataRequests[requestId] = DataRequest({
            requestId: uint256(requestId),
            dataType: "news_sentiment",
            parameters: assets,
            timestamp: block.timestamp,
            requester: msg.sender,
            fulfilled: false
        });

        emit DataRequested(requestId, "news_sentiment", assets, msg.sender);
        return requestId;
    }

    /**
     * @dev Request market data
     */
    function requestMarketData(string[] calldata assets) external onlyAuthorized returns (bytes32 requestId) {
        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(marketDataSource);
        req.setArgs(assets);
        // No secrets needed for CoinGecko free tier

        requestId = _sendRequest(req.encodeCBOR(), subscriptionId, gasLimit, donId);

        dataRequests[requestId] = DataRequest({
            requestId: uint256(requestId),
            dataType: "market_data",
            parameters: assets,
            timestamp: block.timestamp,
            requester: msg.sender,
            fulfilled: false
        });

        emit DataRequested(requestId, "market_data", assets, msg.sender);
        return requestId;
    }

    /**
     * @dev Chainlink Functions callback
     */
    function fulfillRequest(bytes32 requestId, bytes memory response, bytes memory err) internal override {
        DataRequest storage request = dataRequests[requestId];
        if (request.timestamp == 0) {
            revert RequestNotFound();
        }

        request.fulfilled = true;

        if (err.length > 0) {
            // Handle error
            return;
        }

        // Process response based on data type
        if (keccak256(abi.encodePacked(request.dataType)) == keccak256(abi.encodePacked("twitter_sentiment"))) {
            _processSentimentResponse(response, request.parameters[0], "twitter");
        } else if (keccak256(abi.encodePacked(request.dataType)) == keccak256(abi.encodePacked("news_sentiment"))) {
            _processSentimentResponse(response, request.parameters[0], "news");
        } else if (keccak256(abi.encodePacked(request.dataType)) == keccak256(abi.encodePacked("market_data"))) {
            _processMarketDataResponse(response, request.parameters[0]);
        }

        emit DataFulfilled(requestId, request.dataType, response);
    }

    /**
     * @dev Process sentiment response
     */
    function _processSentimentResponse(bytes memory response, string memory asset, string memory source) internal {
        if (response.length >= 64) {
            (int256 sentiment, uint256 volume) = abi.decode(response, (int256, uint256));

            string memory key = string(abi.encodePacked(source, "_", asset));
            sentimentData[key] = SentimentData({
                score: sentiment,
                volume: volume,
                timestamp: block.timestamp,
                source: source
            });

            emit SentimentUpdated(key, sentiment, volume, source);
        }
    }

    /**
     * @dev Process market data response
     */
    function _processMarketDataResponse(bytes memory response, string memory asset) internal {
        if (response.length >= 96) {
            (uint256 price, uint256 volume, int256 priceChange) = abi.decode(response, (uint256, uint256, int256));

            marketData[asset] = MarketData({
                price: price,
                volume24h: volume,
                priceChange24h: priceChange,
                timestamp: block.timestamp,
                asset: asset
            });

            emit MarketDataUpdated(asset, price, volume, priceChange);
        }
    }

    /**
     * @dev Get sentiment data
     */
    function getSentimentData(string memory source, string memory asset) external view returns (SentimentData memory) {
        string memory key = string(abi.encodePacked(source, "_", asset));
        return sentimentData[key];
    }

    /**
     * @dev Get market data
     */
    function getMarketData(string memory asset) external view returns (MarketData memory) {
        return marketData[asset];
    }

    /**
     * @dev Get aggregated sentiment score
     */
    function getAggregatedSentiment(string memory asset) external view returns (int256 score, uint256 totalVolume) {
        SentimentData memory twitterData = sentimentData[string(abi.encodePacked("twitter_", asset))];
        SentimentData memory newsData = sentimentData[string(abi.encodePacked("news_", asset))];

        // Weight: Twitter 40%, News 60%
        uint256 twitterWeight = 4000;
        uint256 newsWeight = 6000;

        score = (twitterData.score * int256(twitterWeight) + newsData.score * int256(newsWeight)) / 10000;
        totalVolume = twitterData.volume + newsData.volume;
    }

    /**
     * @dev Authorize caller
     */
    function setAuthorizedCaller(address caller, bool authorized) external onlyOwner {
        authorizedCallers[caller] = authorized;
    }

    /**
     * @dev Update JavaScript source codes
     */
    function updateSourceCode(string memory dataType, string memory newSource) external onlyOwner {
        if (keccak256(abi.encodePacked(dataType)) == keccak256(abi.encodePacked("twitter_sentiment"))) {
            twitterSentimentSource = newSource;
        } else if (keccak256(abi.encodePacked(dataType)) == keccak256(abi.encodePacked("news_sentiment"))) {
            newsSentimentSource = newSource;
        } else if (keccak256(abi.encodePacked(dataType)) == keccak256(abi.encodePacked("market_data"))) {
            marketDataSource = newSource;
        } else {
            revert InvalidDataType();
        }
    }

    /**
     * @dev Update subscription settings
     */
    function updateSubscription(uint64 _subscriptionId, uint32 _gasLimit) external onlyOwner {
        subscriptionId = _subscriptionId;
        gasLimit = _gasLimit;
    }

    modifier onlyAuthorized() {
        if (!authorizedCallers[msg.sender] && msg.sender != owner()) {
            revert UnauthorizedCaller();
        }
        _;
    }
}

contracts/core/PredictionMarket.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@chainlink/contracts/src/v0.8/automation/AutomationCompatible.sol";
import "@chainlink/contracts/src/v0.8/vrf/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/vrf/VRFConsumerBaseV2.sol";

/**
 * @title PredictionMarket
 * @dev Core prediction market contract with Chainlink integration
 */
contract PredictionMarket is 
    Ownable, 
    ReentrancyGuard, 
    Pausable, 
    AutomationCompatibleInterface,
    VRFConsumerBaseV2
{
    using SafeERC20 for IERC20;

    struct Market {
        uint256 id;
        string question;
        string category;
        uint256 createdAt;
        uint256 endTime;
        uint256 resolutionTime;
        bool resolved;
        bool outcome; // true for YES, false for NO
        uint256 totalYesAmount;
        uint256 totalNoAmount;
        uint256 totalVolume;
        address creator;
        uint256 creatorFee; // basis points (100 = 1%)
        bool active;
    }

    struct Position {
        uint256 marketId;
        address user;
        uint256 yesAmount;
        uint256 noAmount;
        bool claimed;
    }

    struct AgentPrediction {
        address agent;
        uint256 marketId;
        bool prediction; // true for YES, false for NO
        uint256 confidence; // 0-10000 (100.00%)
        uint256 timestamp;
        string reasoning;
    }

    // State variables
    mapping(uint256 => Market) public markets;
    mapping(uint256 => mapping(address => Position)) public positions;
    mapping(uint256 => AgentPrediction[]) public agentPredictions;
    mapping(address => bool) public authorizedAgents;
    mapping(address => uint256) public agentReputation; // 0-10000 (100.00%)

    uint256 public nextMarketId = 1;
    uint256 public platformFee = 250; // 2.5% in basis points
    uint256 public minBetAmount = 0.01 ether;
    uint256 public maxBetAmount = 1000 ether;
    address public feeRecipient;
    IERC20 public bettingToken;

    // Chainlink VRF
    VRFCoordinatorV2Interface private vrfCoordinator;
    uint64 private vrfSubscriptionId;
    bytes32 private vrfKeyHash;
    uint32 private vrfCallbackGasLimit = 100000;
    uint16 private vrfRequestConfirmations = 3;
    uint32 private vrfNumWords = 1;
    mapping(uint256 => uint256) private vrfRequestToMarketId;

    // Chainlink integration contracts
    address public swarmFunctionsContract;
    address public swarmCCIPContract;

    // Events
    event MarketCreated(
        uint256 indexed marketId,
        string question,
        string category,
        uint256 endTime,
        address creator
    );

    event BetPlaced(
        uint256 indexed marketId,
        address indexed user,
        bool side,
        uint256 amount
    );

    event MarketResolved(
        uint256 indexed marketId,
        bool outcome,
        uint256 totalVolume
    );

    event WinningsClaimed(
        uint256 indexed marketId,
        address indexed user,
        uint256 amount
    );

    event AgentPredictionSubmitted(
        uint256 indexed marketId,
        address indexed agent,
        bool prediction,
        uint256 confidence
    );

    event AgentAuthorized(address indexed agent, bool authorized);

    constructor(
        address _vrfCoordinator,
        uint64 _subscriptionId,
        bytes32 _keyHash,
        address _bettingToken,
        address _feeRecipient
    ) Ownable(msg.sender) VRFConsumerBaseV2(_vrfCoordinator) {
        vrfCoordinator = VRFCoordinatorV2Interface(_vrfCoordinator);
        vrfSubscriptionId = _subscriptionId;
        vrfKeyHash = _keyHash;
        bettingToken = IERC20(_bettingToken);
        feeRecipient = _feeRecipient;
    }

    /**
     * @dev Create a new prediction market
     */
    function createMarket(
        string memory _question,
        string memory _category,
        uint256 _endTime,
        uint256 _creatorFee
    ) external returns (uint256) {
        require(_endTime > block.timestamp, "End time must be in the future");
        require(_creatorFee <= 1000, "Creator fee too high"); // Max 10%

        uint256 marketId = nextMarketId++;

        markets[marketId] = Market({
            id: marketId,
            question: _question,
            category: _category,
            createdAt: block.timestamp,
            endTime: _endTime,
            resolutionTime: 0,
            resolved: false,
            outcome: false,
            totalYesAmount: 0,
            totalNoAmount: 0,
            totalVolume: 0,
            creator: msg.sender,
            creatorFee: _creatorFee,
            active: true
        });

        emit MarketCreated(marketId, _question, _category, _endTime, msg.sender);
        return marketId;
    }

    /**
     * @dev Place a bet on a market
     */
    function placeBet(
        uint256 _marketId,
        bool _side, // true for YES, false for NO
        uint256 _amount
    ) external nonReentrant whenNotPaused {
        Market storage market = markets[_marketId];
        require(market.active, "Market not active");
        require(block.timestamp < market.endTime, "Market has ended");
        require(_amount >= minBetAmount && _amount <= maxBetAmount, "Invalid bet amount");

        // Transfer tokens from user
        bettingToken.safeTransferFrom(msg.sender, address(this), _amount);

        // Update position
        Position storage position = positions[_marketId][msg.sender];
        position.marketId = _marketId;
        position.user = msg.sender;

        if (_side) {
            position.yesAmount += _amount;
            market.totalYesAmount += _amount;
        } else {
            position.noAmount += _amount;
            market.totalNoAmount += _amount;
        }

        market.totalVolume += _amount;

        emit BetPlaced(_marketId, msg.sender, _side, _amount);
    }

    /**
     * @dev Submit agent prediction
     */
    function submitAgentPrediction(
        uint256 _marketId,
        bool _prediction,
        uint256 _confidence,
        string memory _reasoning
    ) external {
        require(authorizedAgents[msg.sender], "Not an authorized agent");
        require(markets[_marketId].active, "Market not active");
        require(block.timestamp < markets[_marketId].endTime, "Market has ended");
        require(_confidence <= 10000, "Invalid confidence level");

        agentPredictions[_marketId].push(AgentPrediction({
            agent: msg.sender,
            marketId: _marketId,
            prediction: _prediction,
            confidence: _confidence,
            timestamp: block.timestamp,
            reasoning: _reasoning
        }));

        emit AgentPredictionSubmitted(_marketId, msg.sender, _prediction, _confidence);
    }

    /**
     * @dev Resolve market (automated via Chainlink Automation)
     */
    function resolveMarket(uint256 _marketId, bool _outcome) external onlyOwner {
        Market storage market = markets[_marketId];
        require(market.active, "Market not active");
        require(block.timestamp >= market.endTime, "Market has not ended");
        require(!market.resolved, "Market already resolved");

        market.resolved = true;
        market.outcome = _outcome;
        market.resolutionTime = block.timestamp;

        emit MarketResolved(_marketId, _outcome, market.totalVolume);
    }

    /**
     * @dev Claim winnings from a resolved market
     */
    function claimWinnings(uint256 _marketId) external nonReentrant {
        Market storage market = markets[_marketId];
        Position storage position = positions[_marketId][msg.sender];

        require(market.resolved, "Market not resolved");
        require(!position.claimed, "Already claimed");
        require(position.yesAmount > 0 || position.noAmount > 0, "No position");

        uint256 winningAmount = calculateWinnings(_marketId, msg.sender);
        require(winningAmount > 0, "No winnings");

        position.claimed = true;
        bettingToken.safeTransfer(msg.sender, winningAmount);

        emit WinningsClaimed(_marketId, msg.sender, winningAmount);
    }

    /**
     * @dev Calculate winnings for a user in a market
     */
    function calculateWinnings(uint256 _marketId, address _user) public view returns (uint256) {
        Market storage market = markets[_marketId];
        Position storage position = positions[_marketId][_user];

        if (!market.resolved || position.claimed) {
            return 0;
        }

        uint256 totalPool = market.totalYesAmount + market.totalNoAmount;
        uint256 winningPool = market.outcome ? market.totalYesAmount : market.totalNoAmount;
        uint256 losingPool = market.outcome ? market.totalNoAmount : market.totalYesAmount;
        uint256 userWinningBet = market.outcome ? position.yesAmount : position.noAmount;

        if (userWinningBet == 0 || winningPool == 0) {
            return 0;
        }

        // Calculate proportional winnings from losing pool
        uint256 winningsFromLosingPool = (userWinningBet * losingPool) / winningPool;

        // Apply fees
        uint256 totalFees = (winningsFromLosingPool * (platformFee + market.creatorFee)) / 10000;
        uint256 netWinnings = userWinningBet + winningsFromLosingPool - totalFees;

        return netWinnings;
    }

    /**
     * @dev Chainlink Automation - Check if any markets need resolution
     */
    function checkUpkeep(bytes calldata) external view override returns (bool upkeepNeeded, bytes memory performData) {
        uint256[] memory marketsToResolve = new uint256[](100); // Max 100 markets per check
        uint256 count = 0;

        for (uint256 i = 1; i < nextMarketId && count < 100; i++) {
            Market storage market = markets[i];
            if (market.active && 
                !market.resolved && 
                block.timestamp >= market.endTime) {
                marketsToResolve[count] = i;
                count++;
            }
        }

        upkeepNeeded = count > 0;

        // Encode market IDs to resolve
        uint256[] memory finalArray = new uint256[](count);
        for (uint256 i = 0; i < count; i++) {
            finalArray[i] = marketsToResolve[i];
        }
        performData = abi.encode(finalArray);
    }

    /**
     * @dev Chainlink Automation - Perform market resolution
     */
    function performUpkeep(bytes calldata performData) external override {
        uint256[] memory marketsToResolve = abi.decode(performData, (uint256[]));

        for (uint256 i = 0; i < marketsToResolve.length; i++) {
            uint256 marketId = marketsToResolve[i];
            _requestRandomResolution(marketId);
        }
    }

    /**
     * @dev Request random resolution using Chainlink VRF
     */
    function _requestRandomResolution(uint256 _marketId) internal {
        uint256 requestId = vrfCoordinator.requestRandomWords(
            vrfKeyHash,
            vrfSubscriptionId,
            vrfRequestConfirmations,
            vrfCallbackGasLimit,
            vrfNumWords
        );
        vrfRequestToMarketId[requestId] = _marketId;
    }

    /**
     * @dev Chainlink VRF callback for random resolution
     */
    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords) internal override {
        uint256 marketId = vrfRequestToMarketId[requestId];
        Market storage market = markets[marketId];

        if (!market.resolved && market.active) {
            // Use agent consensus with random tiebreaker
            bool agentConsensus = _getAgentConsensus(marketId);
            bool randomOutcome = (randomWords[0] % 2) == 1;

            // Use agent consensus if available, otherwise random
            bool finalOutcome = agentPredictions[marketId].length > 0 ? agentConsensus : randomOutcome;

            market.resolved = true;
            market.outcome = finalOutcome;
            market.resolutionTime = block.timestamp;

            emit MarketResolved(marketId, finalOutcome, market.totalVolume);
        }
    }

    /**
     * @dev Get agent consensus for market resolution
     */
    function _getAgentConsensus(uint256 _marketId) internal view returns (bool) {
        AgentPrediction[] storage predictions = agentPredictions[_marketId];
        uint256 weightedYes = 0;
        uint256 weightedNo = 0;
        uint256 totalWeight = 0;

        for (uint256 i = 0; i < predictions.length; i++) {
            AgentPrediction storage pred = predictions[i];
            uint256 weight = agentReputation[pred.agent] * pred.confidence;

            if (pred.prediction) {
                weightedYes += weight;
            } else {
                weightedNo += weight;
            }
            totalWeight += weight;
        }

        return weightedYes > weightedNo;
    }

    /**
     * @dev Authorize/deauthorize an agent
     */
    function setAgentAuthorization(address _agent, bool _authorized) external onlyOwner {
        authorizedAgents[_agent] = _authorized;
        if (_authorized && agentReputation[_agent] == 0) {
            agentReputation[_agent] = 5000; // Start with 50% reputation
        }
        emit AgentAuthorized(_agent, _authorized);
    }

    /**
     * @dev Update agent reputation based on performance
     */
    function updateAgentReputation(address _agent, uint256 _newReputation) external onlyOwner {
        require(_newReputation <= 10000, "Invalid reputation");
        agentReputation[_agent] = _newReputation;
    }

    /**
     * @dev Get market details
     */
    function getMarket(uint256 _marketId) external view returns (Market memory) {
        return markets[_marketId];
    }

    /**
     * @dev Get user position in market
     */
    function getPosition(uint256 _marketId, address _user) external view returns (Position memory) {
        return positions[_marketId][_user];
    }

    /**
     * @dev Get agent predictions for market
     */
    function getAgentPredictions(uint256 _marketId) external view returns (AgentPrediction[] memory) {
        return agentPredictions[_marketId];
    }

    /**
     * @dev Emergency pause
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Update platform fee
     */
    function setPlatformFee(uint256 _fee) external onlyOwner {
        require(_fee <= 1000, "Fee too high"); // Max 10%
        platformFee = _fee;
    }

    /**
     * @dev Update betting limits
     */
    function setBettingLimits(uint256 _minAmount, uint256 _maxAmount) external onlyOwner {
        minBetAmount = _minAmount;
        maxBetAmount = _maxAmount;
    }

    /**
     * @dev Withdraw accumulated fees
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = bettingToken.balanceOf(address(this));
        // Calculate total locked in active markets
        uint256 lockedAmount = 0;
        for (uint256 i = 1; i < nextMarketId; i++) {
            if (markets[i].active && !markets[i].resolved) {
                lockedAmount += markets[i].totalYesAmount + markets[i].totalNoAmount;
            }
        }

        uint256 availableFees = balance > lockedAmount ? balance - lockedAmount : 0;
        if (availableFees > 0) {
            bettingToken.safeTransfer(feeRecipient, availableFees);
        }
    }

    /**
     * @dev Set Chainlink Functions contract address
     */
    function setSwarmFunctionsContract(address _functionsContract) external onlyOwner {
        swarmFunctionsContract = _functionsContract;
    }

    /**
     * @dev Set Chainlink CCIP contract address
     */
    function setSwarmCCIPContract(address _ccipContract) external onlyOwner {
        swarmCCIPContract = _ccipContract;
    }

    /**
     * @dev Update market with external data from Chainlink Functions
     */
    function updateMarketWithExternalData(
        uint256 _marketId, 
        int256 sentiment, 
        uint256 volume,
        uint256 price
    ) external {
        require(msg.sender == swarmFunctionsContract, "Only SwarmFunctions can update");
        require(markets[_marketId].active, "Market not active");

        // Calculate prediction score based on real data
        int256 predictionScore = _calculatePredictionScore(_marketId, sentiment, volume, price);

        // Update market metadata with external data and prediction
        emit ExternalDataUpdated(_marketId, sentiment, volume, price);
        emit PredictionScoreUpdated(_marketId, predictionScore);
    }

    /**
     * @dev Calculate prediction score using mathematical models
     */
    function _calculatePredictionScore(
        uint256 _marketId,
        int256 sentiment,
        uint256 volume,
        uint256 price
    ) internal view returns (int256) {
        Market storage market = markets[_marketId];

        // Get current market state
        uint256 totalVolume = market.totalYesAmount + market.totalNoAmount;
        uint256 yesRatio = totalVolume > 0 ? (market.totalYesAmount * 10000) / totalVolume : 5000;

        // Multi-factor prediction model
        int256 score = 0;

        // 1. Sentiment Analysis Weight (30%)
        score += (sentiment * 3000) / 10000;

        // 2. Volume Momentum (20%)
        // Higher volume indicates stronger conviction
        int256 volumeScore = volume > 100 ? int256(2000) : int256(volume) * 20;
        score += volumeScore;

        // 3. Market Imbalance (25%)
        // If market is heavily skewed, predict regression to mean
        int256 imbalanceScore = 0;
        if (yesRatio > 7000) {
            imbalanceScore = -2500; // Oversold, predict correction
        } else if (yesRatio < 3000) {
            imbalanceScore = 2500; // Oversold, predict bounce
        }
        score += imbalanceScore;

        // 4. Price Action (15%)
        // Use price as a momentum indicator
        int256 priceScore = 0;
        if (price > 0) {
            // Simple momentum: recent price action
            priceScore = 1500; // Positive price movement
        }
        score += priceScore;

        // 5. Agent Consensus (10%)
        int256 agentScore = _getWeightedAgentConsensus(_marketId);
        score += (agentScore * 1000) / 10000;

        // Normalize to -10000 to 10000 range
        return _clamp(score, -10000, 10000);
    }

    /**
     * @dev Get weighted agent consensus as score
     */
    function _getWeightedAgentConsensus(uint256 _marketId) internal view returns (int256) {
        AgentPrediction[] storage predictions = agentPredictions[_marketId];
        if (predictions.length == 0) return int256(0);

        int256 weightedSum = 0;
        uint256 totalWeight = 0;

        for (uint256 i = 0; i < predictions.length; i++) {
            AgentPrediction storage pred = predictions[i];
            uint256 weight = agentReputation[pred.agent] * pred.confidence;

            int256 predictionValue = pred.prediction ? int256(10000) : int256(-10000);
            weightedSum += predictionValue * int256(weight);
            totalWeight += weight;
        }

        return totalWeight > 0 ? weightedSum / int256(totalWeight) : int256(0);
    }

    /**
     * @dev Mathematical clamping function
     */
    function _clamp(int256 value, int256 min, int256 max) internal pure returns (int256) {
        if (value < min) return min;
        if (value > max) return max;
        return value;
    }

    /**
     * @dev Get mathematical prediction for market outcome
     */
    function getMathematicalPrediction(uint256 _marketId) external view returns (
        int256 predictionScore,
        uint256 confidence,
        string memory reasoning
    ) {
        Market storage market = markets[_marketId];
        require(market.active, "Market not active");

        // This would use the last external data update
        // For demo, return based on current market state
        uint256 totalVolume = market.totalYesAmount + market.totalNoAmount;
        uint256 yesRatio = totalVolume > 0 ? (market.totalYesAmount * 10000) / totalVolume : 5000;

        predictionScore = _calculateBasicPrediction(yesRatio, totalVolume);
        confidence = _calculateConfidence(totalVolume, agentPredictions[_marketId].length);
        reasoning = _generateReasoning(yesRatio, totalVolume);
    }

    /**
     * @dev Calculate basic prediction based on market state
     */
    function _calculateBasicPrediction(uint256 yesRatio, uint256 totalVolume) internal pure returns (int256) {
        // Mean reversion model: extreme positions tend to correct
        if (yesRatio > 8000) return int256(-5000); // Strong sell signal
        if (yesRatio < 2000) return int256(5000);  // Strong buy signal
        if (yesRatio > 6000) return int256(-2000); // Mild sell signal
        if (yesRatio < 4000) return int256(2000);  // Mild buy signal
        return int256(0); // Neutral
    }

    /**
     * @dev Calculate confidence based on volume and agent participation
     */
    function _calculateConfidence(uint256 totalVolume, uint256 agentCount) internal pure returns (uint256) {
        uint256 volumeScore = totalVolume > 1 ether ? 5000 : (totalVolume * 5000) / 1 ether;
        uint256 agentScore = agentCount > 5 ? 5000 : agentCount * 1000;
        return (volumeScore + agentScore) / 2;
    }

    /**
     * @dev Generate reasoning string
     */
    function _generateReasoning(uint256 yesRatio, uint256 totalVolume) internal pure returns (string memory) {
        if (totalVolume < 0.1 ether) return "Low volume - insufficient data";
        if (yesRatio > 8000) return "Extremely bullish - correction likely";
        if (yesRatio < 2000) return "Extremely bearish - bounce likely";
        if (yesRatio > 6000) return "Bullish trend - some resistance expected";
        if (yesRatio < 4000) return "Bearish trend - some support expected";
        return "Balanced market - neutral outlook";
    }

    /**
     * @dev Cross-chain synchronization function called by CCIP
     */
    function syncFromCrossChain(
        uint256 _marketId,
        uint256 _totalVolume,
        uint256 _avgPrice
    ) external {
        require(msg.sender == swarmCCIPContract, "Only SwarmCCIP can sync");
        require(markets[_marketId].active, "Market not active");

        // Update market with cross-chain data
        emit CrossChainSyncReceived(_marketId, _totalVolume, _avgPrice);
    }

    /**
     * @dev Emergency pause for specific market
     */
    function pauseMarket(uint256 _marketId) external onlyOwner {
        markets[_marketId].active = false;
        emit MarketPaused(_marketId);
    }

    /**
     * @dev Get current price for YES or NO position
     */
    function getPrice(uint256 _marketId, bool _side) public view returns (uint256) {
        Market storage market = markets[_marketId];
        uint256 totalPool = market.totalYesAmount + market.totalNoAmount;

        if (totalPool == 0) {
            return 5000; // 50% initial price (in basis points)
        }

        if (_side) {
            // YES price = (yesAmount / totalAmount) * 10000
            return (market.totalYesAmount * 10000) / totalPool;
        } else {
            // NO price = (noAmount / totalAmount) * 10000
            return (market.totalNoAmount * 10000) / totalPool;
        }
    }

    /**
     * @dev Get market statistics for external systems
     */
    function getMarketStats(uint256 _marketId) external view returns (
        uint256 totalVolume,
        uint256 yesVolume,
        uint256 noVolume,
        uint256 currentYesPrice,
        uint256 currentNoPrice
    ) {
        Market storage market = markets[_marketId];
        return (
            market.totalYesAmount + market.totalNoAmount,
            market.totalYesAmount,
            market.totalNoAmount,
            getPrice(_marketId, true),
            getPrice(_marketId, false)
        );
    }

    // Additional events for integration
    event ExternalDataUpdated(uint256 indexed marketId, int256 sentiment, uint256 volume, uint256 price);
    event CrossChainSyncReceived(uint256 indexed marketId, uint256 totalVolume, uint256 avgPrice);
    event MarketPaused(uint256 indexed marketId);
    event PredictionScoreUpdated(uint256 indexed marketId, int256 predictionScore);

    // Modifiers for backward compatibility (if needed)
    modifier onlyWhenActive(uint256 _marketId) {
        require(markets[_marketId].active, "Market not active");
        _;
    }
}

